

declare module "account" {

	class AccountInformation {
		acoid: string; // {String} Account id.
		group: 'admin' | 'user' | 'guest'; // {String} Account group name.
		alias: string; // {String} Account alias name.
		extra: object; // {Object} Account extra information.
	}

	class Account {
		static info(acoid: string, callback: (error: Error, info: AccountInformation) => void)
		static update(callback: (event: 'add' | 'delete' | 'update', acoid: string) => void)
		static mname(mname: string | undefined, callback?: (error: Error, mname: string) => void)
		static list(callback: (error: Error, list: Array<any>) => void, simple?: boolean)
		static set(acoid: string, alias: string, group: string)
		static set(acoid: string, alias: string, group: string, extra: object, callback?: (error: Error) => void)
		static delete(acoid: string, callback?: (error: Error) => void)

	}
	export = Account

}