declare module "ini" {	
    /**
     * Parse ini file to object.
     *
     * @param data ini file data.
     */
    export function parse(data : string) : object;
	
    /**
     * Stringify object to ini file string.
     *
     * @param ini object
     * @param replacer
     * @param space
     */
	export function stringify(ini : any, replacer?: string, space?: string) : string;
}